 'use client';

 import { useEffect, useState, useRef } from 'react';
 import { v4 as uuidv4 } from 'uuid';
 import { useRouter, useParams } from 'next/navigation';
 import { Bot, User, ArrowLeft, Loader2, Calendar, Clock, Send, Menu, FolderOpen, Activity, Circle, Trash2, GitBranch } from 'lucide-react';
 import { cn } from '@/lib/utils';

 interface Message {
   role: 'user' | 'assistant' | 'system';
   content: string;
   timestamp?: string;
 }

 export default function SessionChatPage() {
   const router = useRouter();
   const params = useParams();
   const projectName = params.project as string;
   const sessionName = params.session as string;

   // Extrair apenas o nome final do projeto (ex: -Users-2a--claude-projetos-diego -> diego)
   // Mas se for -Users-2a--claude (projeto principal), manter o nome completo
   const getSimpleProjectName = (fullName: string): string => {
     // Se for o projeto principal, manter como está
     if (fullName === '-Users-2a--claude') {
       return fullName;
     }

     // Para outros projetos com formato -Users-2a--claude-projetos-[nome]
     if (fullName.startsWith('-Users-2a--claude-projetos-')) {
       return fullName.replace('-Users-2a--claude-projetos-', '');
     }

     // Para formato simples -Users-2a--claude-[nome]
     if (fullName.startsWith('-Users-2a--claude-') && fullName !== '-Users-2a--claude') {
       return fullName.replace('-Users-2a--claude-', '');
     }

     return fullName;
   };

   const [messages, setMessages] = useState<Message[]>([]);
   const [loading, setLoading] = useState(true);
   const [error, setError] = useState<string | null>(null);
   const [sessionInfo, setSessionInfo] = useState<any>(null);
   const [input, setInput] = useState('');
   const [isLoadingMessage, setIsLoadingMessage] = useState(false);
   const [sidebarOpen, setSidebarOpen] = useState(true);
   const [liveMessages, setLiveMessages] = useState<any[]>([]);
   const [isProcessing, setIsProcessing] = useState(false);
   const [goStatus, setGoStatus] = useState<boolean | null>(null);
   const [pyStatus, setPyStatus] = useState<boolean | null>(null);
   const [sessions, setSessions] = useState<any[]>([]);
   const messagesEndRef = useRef<HTMLDivElement>(null);

   const scrollToBottom = () => {
     messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
   };

   useEffect(() => {
     scrollToBottom();
   }, [messages]);

   // Check backend status
   useEffect(() => {
     const checkStatus = async () => {
       // Check Go backend
       try {
         const response = await fetch('http://localhost:8080/health');
         setGoStatus(response.ok);
       } catch (error) {
         setGoStatus(false);
       }

       // Check Python backend
       try {
         const response = await fetch('http://localhost:8080/api/health');
         setPyStatus(response.ok);
       } catch (error) {
         setPyStatus(false);
       }
     };

     checkStatus();
     const interval = setInterval(checkStatus, 5000);
     return () => clearInterval(interval);
   }, []);

   // Load sessions list
   useEffect(() => {
     const loadSessions = async () => {
       if (!projectName) return;

       try {
         const response = await fetch(`http://localhost:8080/api/projects/${encodeURIComponent(projectName)}/sessions`);
         if (response.ok) {
           const data = await response.json();
           setSessions(data.sessions || []);
         }
       } catch (error) {
         console.error('Error loading sessions:', error);
       }
     };

     loadSessions();
   }, [projectName]);

   const [hasInitialized, setHasInitialized] = useState(false);

   useEffect(() => {
     if (projectName && sessionName) {
       fetchSession();
     }
   }, [projectName, sessionName]);

   // Enviar mensagem inicial para novos projetos
   useEffect(() => {
     if (!loading && messages.length === 0 && !hasInitialized && projectName) {
       // Se não há mensagens e ainda não inicializou, enviar \"oi\" automaticamente
       setHasInitialized(true);
       sendInitialMessage();
     }
   }, [loading, messages.length, hasInitialized, projectName]);

   // Monitor live JSONL lines
   useEffect(() => {
     const fetchLiveActivity = async () => {
       try {
         const response = await fetch('http://localhost:8080/api/live-session');
         if (response.ok) {
           const data = await response.json();
           // Handle both cases: when file exists with lines, and when it doesn't
           if (data.lines) {
             setLiveMessages(data.lines);

             // Check if processing based on last messages
             if (data.lines.length > 0) {
               try {
                 // Look at the last few messages to determine state
                 const recentLines = data.lines.slice(-5); // Check last 5 messages
                 let hasUserMessage = false;
                 let lastAssistantMessage = null;
                 let hasToolUse = false;

                 for (const line of recentLines) {
                   try {
                     const msgData = JSON.parse(line);

                     if (msgData.type === 'user') {
                       hasUserMessage = true;
                       // Reset assistant message when we see a new user message
                       lastAssistantMessage = null;
                       hasToolUse = false;
                     } else if (msgData.type === 'assistant') {
                       lastAssistantMessage = msgData.message;
                       // Check if this assistant message has tool use
                       if (msgData.message?.content?.some((c: any) => c.type === 'tool_use')) {
                         hasToolUse = true;
                       }
                     }
                   } catch {
                     // Ignore parse errors for individual lines
                   }
                 }

                 // Determine if we're still processing
                 if (hasUserMessage && !lastAssistantMessage) {
                   // User message but no assistant response yet
                   setIsProcessing(true);
                 } else if (hasToolUse) {
                   // Has tool use, likely still processing
                   setIsProcessing(true);
                 } else if (lastAssistantMessage) {
                   // Check if assistant has meaningful text response
                   const textContent = lastAssistantMessage.content?.find((c: any) => c.type === 'text');
                   if (textContent?.text && textContent.text.length > 50) {
                     // Has substantial text response, processing is done
                     setIsProcessing(false);
                   } else {
                     // Still waiting for full response
                     setIsProcessing(true);
                   }
                 } else {
                   // Default to not processing
                   setIsProcessing(false);
                 }
               } catch (e) {
                 // Ignore parse errors
                 console.error('Error parsing activity:', e);
               }
             }
           }
         }
       } catch (error) {
         console.error('Error fetching live activity:', error);
       }
     };

     // Fetch immediately
     fetchLiveActivity();

     // Then fetch every 1 second for real-time updates
     const interval = setInterval(fetchLiveActivity, 1000);

     return () => clearInterval(interval);
   }, []);

   const fetchSession = async () => {
     try {
       // Constrói o caminho da API usando os parâmetros
       const apiPath = `/api/sessions/${encodeURIComponent(projectName)}/${encodeURIComponent(sessionName)}`;
       const response = await fetch(apiPath);

       if (!response.ok) {
         // Se o arquivo não existe, isso é normal - vamos permitir criar uma nova conversa
         console.log('Sessão não existe ainda, permitindo nova conversa');
         setMessages([]);
         setSessionInfo(null);
       } else {
         const data = await response.json();

         if (data.messages) {
           setMessages(data.messages);
           setSessionInfo({
             createdAt: data.messages[0]?.timestamp,
             messageCount: data.messages.length
           });
         }
       }
     } catch (err) {
       console.error('Erro:', err);
       // Não mostrar erro - permitir criar nova conversa
       setMessages([]);
       setSessionInfo(null);
     } finally {
       setLoading(false);
     }
   };

   const sendInitialMessage = async () => {
     // Verificar se já não há mensagens
     if (messages.length > 0) return;

     const initialMessage = \"oi\";
     const userMessage: Message = {
       role: 'user',
       content: initialMessage,
       timestamp: new Date().toISOString(),
     };

     setMessages([userMessage]);
     setIsLoadingMessage(true);
     setIsProcessing(true);

     try {
       const response = await fetch('http://localhost:8080/api/chat', {
         method: 'POST',
         headers: {
           'Content-Type': 'application/json',
         },
         body: JSON.stringify({
           message: initialMessage,
           session_id: sessionName.replace('.jsonl', ''),
           project_name: projectName, // Enviar nome completo para o backend encontrar a sessão
         }),
       });

       if (!response.ok) throw new Error('Failed to send initial message');

       const reader = response.body?.getReader();
       const decoder = new TextDecoder();

       let assistantMessage: Message = {
         role: 'assistant',
         content: '',
         timestamp: new Date().toISOString(),
       };

       setMessages(prev => [...prev, assistantMessage]);

       while (true) {
         const { done, value } = await reader!.read();
         if (done) break;

         const chunk = decoder.decode(value);
         const lines = chunk.split('\n');

         for (const line of lines) {
           if (line.startsWith('data: ')) {
             try {
               const data = JSON.parse(line.slice(6));
               if (data.type === 'text') {
                 assistantMessage.content += data.content;
                 setMessages(prev =>
                   prev.map(msg =>
                     msg === assistantMessage || msg.timestamp === assistantMessage.timestamp
                       ? { ...msg, content: assistantMessage.content }
                       : msg
                   )
                 );
               } else if (data.type === 'done') {
                 setIsProcessing(false);
                 if (data.session_id) {
                   console.log('Initial session created:', data.session_id);
                 }
               }
             } catch (e) {
               // Ignore parsing errors
             }
           }
         }
       }
     } catch (error: any) {
       console.error('Error sending initial message:', error);

       // Detecção específica de erros
       let errorMsg = '❌ Erro desconhecido ao iniciar sessão';
       let shouldRetry = false;

       if (error.status === 429 || error?.response?.status === 429) {
         errorMsg = '⏱️ Muitas requisições. Aguarde 1 minuto antes de tentar novamente.';
       } else if (error.status === 503 || error.status === 502 || error?.response?.status === 503 || error?.response?.status === 502) {
         errorMsg = '⚠️ Serviço temporariamente indisponível. Tentando novamente em 3 segundos...';
         shouldRetry = true;
       } else if (error.status === 401 || error.status === 403 || error?.response?.status === 401 || error?.response?.status === 403) {
         errorMsg = '🔒 Não autorizado. Verifique suas credenciais.';
       } else if (!navigator.onLine) {
         errorMsg = '🔌 Sem conexão com internet. Verifique sua rede e tente novamente.';
       } else if (error.name === 'AbortError' || error.name === 'TimeoutError') {
         errorMsg = '⏱️ Requisição cancelada por timeout. Tente novamente.';
       } else if (error.message === 'Failed to send initial message' || error.message?.includes('fetch')) {
         errorMsg = '🔌 Erro de conexão com o servidor. Verifique se o backend está rodando.';
       } else if (error.message) {
         errorMsg = `❌ Erro: ${error.message}`;
       }

       // Adicionar mensagem de erro
       setMessages(prev => [...prev, {
         role: 'assistant',
         content: errorMsg,
         timestamp: new Date().toISOString(),
       }]);

       // Retry automático para erros transientes
       if (shouldRetry) {
         setTimeout(() => {
           sendInitialMessage();
         }, 3000);
       }
     } finally {
       setIsLoadingMessage(false);
       setIsProcessing(false);
     }
   };

   const sendMessage = async () => {
     if (!input.trim() || isLoadingMessage) return;

     const messageText = input; // Salvar o texto antes de limpar
     const userMessage: Message = {
       role: 'user',
       content: messageText,
       timestamp: new Date().toISOString(),
     };

     setMessages(prev => [...prev, userMessage]);
     setInput('');
     setIsLoadingMessage(true);
     setIsProcessing(true); // Start processing indicator

     try {
       const response = await fetch('http://localhost:8080/api/chat', {
         method: 'POST',
         headers: {
           'Content-Type': 'application/json',
         },
         body: JSON.stringify({
           message: messageText, // Usar o texto salvo
           session_id: sessionName.replace('.jsonl', ''), // Remove .jsonl e passa o ID correto da sessão
           project_name: projectName, // Enviar nome completo para o backend encontrar a sessão // Envia apenas o nome simples (ex: diego)
         }),
       });

       if (!response.ok) throw new Error('Failed to send message');

       const reader = response.body?.getReader();
       const decoder = new TextDecoder();

       let assistantMessage: Message = {
         role: 'assistant',
         content: '',
         timestamp: new Date().toISOString(),
       };

       setMessages(prev => [...prev, assistantMessage]);

       while (true) {
         const { done, value } = await reader!.read();
         if (done) break;

         const chunk = decoder.decode(value);
         const lines = chunk.split('\n');

         for (const line of lines) {
           if (line.startsWith('data: ')) {
             try {
               const data = JSON.parse(line.slice(6));
               if (data.type === 'text') {
                 assistantMessage.content += data.content;
                 setMessages(prev =>
                   prev.map(msg =>
                     msg === assistantMessage || msg.timestamp === assistantMessage.timestamp
                       ? { ...msg, content: assistantMessage.content }
                       : msg
                   )
                 );
               } else if (data.type === 'done') {
                 // Received done signal from backend
                 setIsProcessing(false);
                 if (data.session_id) {
                   console.log('Session completed:', data.session_id);
                 }
               }
             } catch (e) {
               // Ignore parsing errors
             }
           }
         }
       }
     } catch (error: any) {
       console.error('Error sending message:', error);

       // Detecção específica de erros
       let errorMsg = '❌ Erro desconhecido';
       let shouldRetry = false;

       if (error.status === 429 || error?.response?.status === 429) {
         errorMsg = '⏱️ Muitas requisições. Aguarde 1 minuto antes de tentar novamente.';
       } else if (error.status === 503 || error.status === 502 || error?.response?.status === 503 || error?.response?.status === 502) {
         errorMsg = '⚠️ Serviço temporariamente indisponível. Tentando novamente em 3 segundos...';
         shouldRetry = true;
       } else if (error.status === 401 || error.status === 403 || error?.response?.status === 401 || error?.response?.status === 403) {
         errorMsg = '🔒 Não autorizado. Verifique suas credenciais.';
       } else if (!navigator.onLine) {
         errorMsg = '🔌 Sem conexão com internet. Verifique sua rede e tente novamente.';
       } else if (error.name === 'AbortError' || error.name === 'TimeoutError') {
         errorMsg = '⏱️ Requisição cancelada por timeout. Tente novamente.';
       } else if (error.message === 'Failed to send message' || error.message?.includes('fetch')) {
         errorMsg = '🔌 Erro de conexão com o servidor. Verifique se o backend está rodando.';
       } else if (error.message) {
         errorMsg = `❌ Erro: ${error.message}`;
       }

       // Adicionar mensagem de erro
       setMessages(prev => [...prev, {
         role: 'assistant',
         content: errorMsg,
         timestamp: new Date().toISOString(),
       }]);

       // Retry automático para erros transientes
       if (shouldRetry) {
         setTimeout(() => {
           // Re-enviar a última mensagem
           sendMessage();
         }, 3000);
       }
     } finally {
       setIsLoadingMessage(false);
       setIsProcessing(false);
     }
   };

   const handleForkSession = async () => {
     try {
       const forkId = crypto.randomUUID();
       const response = await fetch('http://localhost:8080/api/fork-session', {
         method: 'POST',
         headers: {
           'Content-Type': 'application/json',
         },
         body: JSON.stringify({
           source_session: sessionName.replace('.jsonl', ''),
           fork_session: forkId,
           project_name: projectName,
         }),
       });

       if (response.ok) {
         const data = await response.json();
         console.log('🔀 Fork criado:', data);
         // Navegar para o fork
         router.push(`/projects/${projectName}/${forkId}`);
       } else {
         console.error('Erro ao criar fork:', response.status, response.statusText);
       }
     } catch (error) {
       console.error('Error forking session:', error);
     }
   };

   const handleDeleteSession = async () => {
     try {
       const response = await fetch(
         `http://localhost:8080/api/projects/${projectName}/sessions/${sessionName.replace('.jsonl', '')}`,
         {
           method: 'DELETE',
         }
       );

       if (response.ok) {
         // Redirecionar para a home após deletar
         router.push('/');
       } else {
         console.error('Erro ao deletar sessão:', response.status, response.statusText);
       }
     } catch (error) {
       console.error('Error deleting session:', error);
     }
   };

   const formatDate = (dateString: string | undefined) => {
     if (!dateString) return '';
     const date = new Date(dateString);
     return new Intl.DateTimeFormat('pt-BR', {
       day: '2-digit',
       month: 'long',
       year: 'numeric',
       hour: '2-digit',
       minute: '2-digit'
     }).format(date);
   };

   const formatMessageTime = (dateString: string | undefined) => {
     if (!dateString) return '';
     const date = new Date(dateString);
     return new Intl.DateTimeFormat('pt-BR', {
       hour: '2-digit',
       minute: '2-digit'
     }).format(date);
   };

   const decodedProjectName = decodeURIComponent(projectName).replace(/-/g, ' ');
   const decodedSessionName = decodeURIComponent(sessionName).replace('.jsonl', '');

   return (
     <div className=\"flex h-screen bg-background\">
       {/* Sidebar */}
       <div className={cn(
         \"w-64 bg-card border-r border-border flex flex-col transition-all duration-300\",
         !sidebarOpen && \"-ml-64\"
       )}>
         {/* Sidebar Header */}
         <div className=\"p-4 border-b border-border space-y-2\">
           <button
             onClick={() => router.push('/')}
             className=\"w-full flex items-center justify-center gap-2 px-4 py-2.5 bg-primary text-primary-foreground rounded-lg hover:opacity-90 transition-opacity\"
           >
             <FolderOpen className=\"w-4 h-4\" />
             <span className=\"text-sm font-medium\">Home</span>
           </button>

           {/* Project Name Input */}
           <div className=\"space-y-1\">
             <label className=\"text-xs text-muted-foreground\">Ir para projeto:</label>
             <div className=\"flex gap-2\">
               <input
                 type=\"text\"
                 placeholder=\"Nome do projeto...\"
                 className=\"flex-1 px-3 py-2 text-sm bg-background border border-input rounded-lg focus:outline-none focus:ring-2 focus:ring-ring focus:border-transparent\"
                 onKeyPress={(e) => {
                   if (e.key === 'Enter') {
                     const projectName = (e.target as HTMLInputElement).value.trim();
                     if (projectName) {
                       // Se já tem o formato completo, usa direto
                       // Senão, adiciona o prefixo padrão
                       const formattedName = projectName.startsWith('-Users-')
                         ? projectName
                         : `-Users-2a--claude-${projectName}`;
                       router.push(`/projects/${formattedName}/${uuidv4()}`);
                     }
                   }
                 }}
               />
               <button
                 onClick={() => router.push('/')}
                 className=\"p-2 bg-secondary rounded-lg hover:opacity-90 transition-opacity\"
                 title=\"Home\"
               >
                 <Bot className=\"w-4 h-4\" />
               </button>
             </div>
           </div>
         </div>

         {/* Live Session Monitor */}
         <div className=\"flex-1 overflow-y-auto p-4\">
           {/* Loading indicator */}
           {isProcessing && (
             <div className=\"mb-3 p-2 bg-blue-500/10 rounded-lg border border-blue-500/20 animate-pulse\">
               <div className=\"flex items-center gap-2\">
                 <div className=\"w-2 h-2 bg-blue-500 rounded-full animate-bounce\" style={{ animationDelay: '0ms' }}></div>
                 <div className=\"w-2 h-2 bg-blue-500 rounded-full animate-bounce\" style={{ animationDelay: '150ms' }}></div>
                 <div className=\"w-2 h-2 bg-blue-500 rounded-full animate-bounce\" style={{ animationDelay: '300ms' }}></div>
                 <span className=\"text-xs text-blue-500 font-medium\">Processando...</span>
               </div>
             </div>
           )}

           {/* Sessions List */}
           <div className=\"mt-4\">
             <h3 className=\"text-xs font-semibold text-muted-foreground mb-2 px-2\">Sessões do Projeto</h3>
             <div className=\"space-y-1\">
               {sessions.length > 0 ? (
                 sessions.map((session) => {
                   const isActive = sessionName.replace('.jsonl', '') === session.id;
                   return (
                     <button
                       key={session.id}
                       onClick={() => router.push(`/projects/${projectName}/${session.id}`)}
                       className={cn(
                         \"w-full text-left px-3 py-2 rounded-lg text-xs transition-colors\",
                         isActive
                           ? \"bg-primary text-primary-foreground font-medium\"
                           : \"bg-muted/50 hover:bg-muted text-foreground/80 hover:text-foreground\"
                       )}
                     >
                       <div className=\"flex items-center gap-2\">
                         <Circle className={cn(\"w-2 h-2\", isActive ? \"fill-current\" : \"\")} />
                         <span className=\"truncate\">{session.id.slice(0, 8)}...</span>
                       </div>
                     </button>
                   );
                 })
               ) : (
                 <p className=\"text-xs text-muted-foreground text-center py-2\">
                   Nenhuma sessão encontrada
                 </p>
               )}
             </div>
           </div>
         </div>
       </div>

       {/* Main Content */}
       <div className=\"flex-1 flex flex-col\">
         {/* Header */}
         <div className=\"px-6 py-3 border-b flex items-center justify-between\">
           <div className=\"flex items-center gap-3\">
             <button
               onClick={() => setSidebarOpen(!sidebarOpen)}
               className=\"p-2 hover:bg-accent rounded-lg transition-colors\"
             >
               <Menu className=\"w-5 h-5\" />
             </button>
             <button
               onClick={() => router.back()}
               className=\"hover:opacity-70 transition-opacity\"
             >
               <ArrowLeft className=\"w-5 h-5\" />
             </button>
             <h1 className=\"text-lg font-medium\">...{decodedSessionName.slice(-8)}</h1>
           </div>

           {/* Backend Status Indicator */}
           <div className=\"flex items-center gap-3\">
             {(goStatus !== null || pyStatus !== null) && (
               <>
                 {goStatus !== null && (
                   <div className=\"flex items-center gap-1.5\">
                     <Circle
                       className={cn(
                         \"w-2 h-2\",
                         goStatus ? \"fill-green-500 text-green-500\" : \"fill-red-500 text-red-500\"
                       )}
                     />
                     <span className={cn(
                       \"text-xs font-mono\",
                       goStatus ? \"text-green-600\" : \"text-red-600\"
                     )}>
                       :8000
                     </span>
                   </div>
                 )}
                 {pyStatus !== null && (
                   <div className=\"flex items-center gap-1.5\">
                     <Circle
                       className={cn(
                         \"w-2 h-2\",
                         pyStatus ? \"fill-green-500 text-green-500\" : \"fill-red-500 text-red-500\"
                       )}
                     />
                     <span className={cn(
                       \"text-xs font-mono\",
                       pyStatus ? \"text-green-600\" : \"text-red-600\"
                     )}>
                       :8080
                     </span>
                   </div>
                 )}
               </>
             )}
             <button
               onClick={handleForkSession}
               className=\"p-2 hover:bg-blue-500/10 rounded-lg transition-colors group\"
               title=\"Fork Conversation - Criar ramificação desta conversa\"
             >
               <GitBranch className=\"w-4 h-4 text-muted-foreground group-hover:text-blue-500\" />
             </button>
             <button
               onClick={handleDeleteSession}
               className=\"p-2 hover:bg-destructive/10 rounded-lg transition-colors group\"
               title=\"Deletar sessão\"
             >
               <Trash2 className=\"w-4 h-4 text-muted-foreground group-hover:text-destructive\" />
             </button>
           </div>
         </div>

         {/* Chat Messages */}
         <div className=\"flex-1 overflow-y-auto\">
         <div className=\"max-w-4xl mx-auto px-4 py-8\">
           {loading ? (
             <div className=\"flex items-center justify-center py-12\">
               <Loader2 className=\"w-8 h-8 animate-spin text-primary\" />
             </div>
           ) : messages.length === 0 ? (
             <div className=\"text-center py-12 space-y-6\">
               <div className=\"inline-flex items-center justify-center w-16 h-16 rounded-full bg-primary/10\">
                 <Bot className=\"w-8 h-8 text-primary\" />
               </div>
               <div className=\"space-y-2\">
                 <h2 className=\"text-2xl font-semibold\">Olá! Como posso ajudar?</h2>
                 <p className=\"text-muted-foreground max-w-md mx-auto\">
